package org.fouryouandme.main.tasks

import arrow.core.toOption
import arrow.fx.ForIO
import com.giacomoparisi.recyclerdroid.core.DroidItem
import org.fouryouandme.core.arch.android.BaseViewModel
import org.fouryouandme.core.arch.deps.Runtime
import org.fouryouandme.core.arch.error.handleAuthError
import org.fouryouandme.core.arch.navigation.Navigator
import org.fouryouandme.core.arch.navigation.RootNavController
import org.fouryouandme.core.cases.CachePolicy
import org.fouryouandme.core.cases.configuration.ConfigurationUseCase
import org.fouryouandme.core.entity.configuration.Configuration
import org.fouryouandme.core.entity.configuration.HEXGradient
import org.fouryouandme.core.entity.task.Task
import org.fouryouandme.core.ext.unsafeRunAsync
import org.fouryouandme.main.items.DateItem
import org.fouryouandme.main.items.toItems
import org.threeten.bp.ZonedDateTime

class TasksViewModel(
    navigator: Navigator,
    runtime: Runtime<ForIO>
) : BaseViewModel<
        ForIO,
        TasksState,
        TasksStateUpdate,
        TasksError,
        TasksLoading>
    (TasksState(), navigator, runtime) {

    /* --- data --- */

    fun initialize(rootNavController: RootNavController): Unit =
        runtime.fx.concurrent {

            !showLoading(TasksLoading.Initialization)

            val configuration =
                !ConfigurationUseCase.getConfiguration(runtime, CachePolicy.MemoryFirst)
                    .handleAuthError(runtime, rootNavController, navigator)

            !configuration.fold(
                { setError(it, TasksError.Initialization) },
                {
                    setState(
                        state().copy(
                            configuration = it.toOption(),
                            tasks = taskMock(it)
                        ),
                        TasksStateUpdate.Initialization(it, taskMock(it))
                    )
                }
            )

            !hideLoading(TasksLoading.Initialization)

        }.unsafeRunAsync()


    // TODO: remove mock
    private fun taskMock(configuration: Configuration): List<DroidItem> {

        val list = mutableListOf<DroidItem>()

        list.add(
            DateItem(
                configuration,
                ZonedDateTime.now()
            )
        )

        val tasks =
            listOf(
                Task(
                    "1",
                    "",
                    "Walk Test",
                    "Complete your 3-Minute Walk Test",
                    "START NOW",
                    HEXGradient("#FF94C5", "#E167AC"),
                    ZonedDateTime.now()
                ),
                Task(
                    "2",
                    "",
                    "It's time to record a video diary",
                    "Your weekly log explaining your changes and feelings.",
                    "VIDEO",
                    HEXGradient("#25AEC2", "#0B99AE"),
                    ZonedDateTime.now()
                ),
                Task(
                    "3",
                    "",
                    "Gait Activity",
                    "measure your gait and balance as you walk and stand still",
                    "START NOW",
                    HEXGradient("#FA8886", "#F57286"),
                    ZonedDateTime.now()
                ),
                Task(
                    "4",
                    "",
                    "We didn't get any steps from your Fitbit yesterday",
                    "Sync now or click to visit the FAQ page for help",
                    "FAQ",
                    HEXGradient("#918EF2", "#918EF2"),
                    ZonedDateTime.now()
                )

            ).toItems(configuration).toMutableList()

        list.addAll(tasks)

        return list
    }

}